/** Type definitions for tasks */

import type { RetryConfig, Step } from '../../schema';

/** Re-export Step for consumers */
export type { RetryConfig, Step };

/** Step input - id is generated by the RPC layer */
export type StepInput = Omit<Step, 'id'>;

export type Task = {
  id: string;
  version: number;
  name: string;
  description: string;
  project_id: string | null;
  library_id: string | null;
  tags: string[] | null;
  input_schema: object;
  output_schema: object;
  steps: Step[];
  retry: RetryConfig | null;
  timeout_ms: number | null;
  content_hash: string | null;
  created_at: string;
  updated_at: string;
};

export type TaskInput = {
  version?: number;
  name: string;
  description?: string;
  project_id?: string;
  library_id?: string;
  tags?: string[];
  input_schema: object;
  output_schema: object;
  steps: StepInput[];
  retry?: RetryConfig;
  timeout_ms?: number;
  autoversion?: boolean;
};
